(()=>{const e=document.querySelector(".search-box");function t(e){document.querySelector(".location .city").innerText=`${e.name}, ${e.sys.country}`;const t=new Date;var n;document.querySelector(".location .date").innerText=`${["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"][(n=t).getDay()]} ${n.getDate()} ${["January","February","March","April","May","June","July","August","September","October","November","December"][n.getMonth()]} ${n.getFullYear()}`,document.querySelector(".current .temp").innerHTML=Math.round(e.main.temp)+"<span>°c</span>",document.querySelector(".current .weather").innerText=e.weather[0].main,document.querySelector(".hi-low").innerText=`${Math.round(e.main.temp_min)}°c / ${Math.round(e.main.temp_max)}°c`}e.addEventListener("keypress",(function(n){var r;13===n.keyCode&&(r=e.value,fetch(`https://api.openweathermap.org/data/2.5/weather?q=${r}&units=metric&APPID=71a1d54910fc34b8989358aaf59ab093`).then((e=>e.json())).then(t))}))})();